import logo from './logo.svg';
import {useEffect, useState} from 'react';
import React, {BrowserRouter, Route, Routes} from 'react-router-dom';
import NavBar from './NavBar';
import Axios from 'axios';
import LoginForm from './auth/LoginForm';
import MainPageOut from './MainPageOut';
import Search from './Search';
import JoblyApi from './api';
import './App.css';
import SignupForm from './auth/SignupForm';
import useLocalStorage from './useLocalStorage';
import Homepage from './home/Homepage';
import jwt_decode from 'jwt-decode';
import Profile from './home/Homepage';

export const TOKEN_STORAGE_ID = "jobly-token";

function App() {
  const [userLoaded, setUserLoaded] = useState(false);
  const [token, setToken] = useLocalStorage(TOKEN_STORAGE_ID);
  const [currentUser, setCurrentUser] = useState(null);
  const [applicationIds, setApplicationIds] = useState(new Set([]));

  useEffect(() => {
    //This is an attempt to confirm if there is someone already signed in.
    async function getCurrentUser() {
      //This statement allows for the token to be truthy (meaning someont is singed in with a current token, or falsey menaing there is no token and no one is signed in at the moment)
      if(token){
        try{
          let {username} = jwt_decode(token);
          JoblyApi.token = token;
          console.log(token);
          const currentUser = await JoblyApi.getUser(username);
          setCurrentUser(currentUser);
          setApplicationIds(new Set(currentUser.applications));
        }catch(err){
          console.err("Problem with authentication token.", err);
          setCurrentUser(null);
        }
      }
      setUserLoaded(true);
    }
    setUserLoaded(false);
    getCurrentUser();
  }, [token])

  async function signUp(data) {
    try{
      let token = await JoblyApi.signUp(data);
      setToken(token)
      return{success: true};
    }catch(err){
      console.err("sign-up failed. Please try again", err)
      return {success: false, err}
    }
  }

  async function login(data){
    try{
      let token = await JoblyApi.login(data);
      setToken(token)
      return{success: true};
    }catch(err){
      console.err("Login failed. Please try again", err)
      return {success: false, err}
    }
  }

  function logout(){
    setCurrentUser(null);
    setToken(null);
  }

  function hasApplied(id){
    return applicationIds && applicationIds.has(id)
  }

  const needsToApply = (id) => {
    if(hasApplied(id)) return;
    JoblyApi.applyToJob(currentUser.username, id);
    setApplicationIds(new Set([...applicationIds, id]));
  }

  return (
    <div className="App">
      <BrowserRouter>
        <NavBar />

        <main className="mainbackground" >
          <Card inverse>
          <Routes>
            //Switch has been deprecated, this is why Route replaced Switch with some syntax changes.
            <Route path="/" element={<Homepage login={login} register={signUp} currentUser={currentUser} setCurrentUser={setCurrentUser} hasApplied={hasApplied} needsToApply={needsToApply}  />}/>
            {/* <Route path="/out" element={<MainPageOut username="sharon" data="data" />}/> */}
            <Route path="/login" element={<LoginForm login={login} />}/>
            <Route path="/signup" element={<SignupForm register={signUp} />}/>
            <Route path="/profile" element={<Profile login={login} register={signUp} currentUser={currentUser} setCurrentUser={setCurrentUser} hasApplied={hasApplied} needsToApply={needsToApply}  />}/>
            <Route path="/companies" element={<Search login={login} register={signUp} currentUser={currentUser} setCurrentUser={setCurrentUser} hasApplied={hasApplied} needsToApply={needsToApply} />}/>
            <Route path="/companies/id"></Route>
            {/* <Route path="/jobs" element={<Search title="jobstest" type="jobs" />}/> */}
          </Routes>
          </Card>
        </main>

      </BrowserRouter>
    </div>
  );
}

export default App;
