import logo from './logo.svg';
import useEffect from 'react';
import React, {BrowserRouter, Route, Routes} from 'react-router-dom';
import NavBar from './NavBar';
import Axios from 'axios';
import MainPageIn from './MainPageIn';
import MainPageOut from './MainPageOut';
import Search from './Search';
import JoblyApi from './api';
import './App.css';
import SignupForm from './auth/SignupForm';
import useLocalStorage from './useLocalStorage';
import Homepage from './home/Homepage';

export const TOKEN_STORAGE_ID = "jobly-token";

function App() {
  const [token, setToken] = useLocalStorage(TOKEN_STORAGE_ID)



  async function signUp(data) {
    try{
      let token = await JoblyApi.signUp(data);
      setToken(token)
      return{success: true};
    }catch(err){
      console.error("sign-up failed. Please try again", err)
      return {success: false, err}
    }
  }

  return (
    <div className="App">
      <BrowserRouter>
        <NavBar />

        <main className="mainbackground" >
          <Routes>
            //Switch has been deprecated, this is why Route replaced Switch with some syntax changes.
            <Route path="/" element={<Homepage username="sharon" data="data" />}/>
            {/* <Route path="/out" element={<MainPageOut username="sharon" data="data" />}/>
            // <Route path="/login" element={<Login username="sharon" />}/> */}
            <Route path="/signup" element={<SignupForm register={signUp} />}/>
            {/* <Route path="/profile" element={<Profile username="sharon" />}/>
            <Route path="/companies" element={<Search title="companiestest" type="Sharon" />}/>
            <Route path="/companies/id"></Route> */}
            {/* <Route path="/jobs" element={<Search title="jobstest" type="jobs" />}/> */}
          </Routes>
        </main>

      </BrowserRouter>
    </div>
  );
}

export default App;
